			m_scrInMemoryCache.AddRunToMockedPara(para2Rev, "Then I know that the LORD saves his anointed king.", ScrStyleNames.NormalParagraph);
			m_scrInMemoryCache.AddRunToMockedPara(para3Rev, "and He will answer him from his holy heaven.", ScrStyleNames.NormalParagraph);
			sectionRev.AdjustReferences();

			// Detect differences and verify them
			m_bookMerger.DetectDifferences(null);
			Assert.AreEqual(4, m_bookMerger.Differences.Count);

