			StTxtPara para1Rev = m_scrInMemoryCache.AddParaToMockedSectionContent(sectionRev.Hvo, ScrStyleNames.IntroParagraph);
			m_scrInMemoryCache.AddRunToMockedPara(para1Rev, "A body of text about A", Cache.DefaultVernWs);
			int lenPara1Rev = para1Rev.Contents.Length;

			StTxtPara para2Rev = m_scrInMemoryCache.AddParaToMockedSectionContent(sectionRev.Hvo, ScrStyleNames.IntroParagraph);
			m_scrInMemoryCache.AddRunToMockedPara(para2Rev, "More discourse on the second letter B", Cache.DefaultVernWs);
			int lenPara2Rev = para2Rev.Contents.Length;

			StTxtPara para3Rev = m_scrInMemoryCache.AddParaToMockedSectionContent(sectionRev.Hvo, ScrStyleNames.IntroParagraph);
			m_scrInMemoryCache.AddRunToMockedPara(para3Rev, "Finally, some conclusions about the letter C", Cache.DefaultVernWs);
			int lenPara3Rev = para3Rev.Contents.Length;

			// add 3 paragraphs of text to the "current" intro section
			StTxtPara para1Curr = m_scrInMemoryCache.AddParaToMockedSectionContent(sectionCur.Hvo, ScrStyleNames.IntroParagraph);
			m_scrInMemoryCache.AddRunToMockedPara(para1Curr, "A body of text about A", Cache.DefaultVernWs);
			int lenPara1Curr = para1Curr.Contents.Length;

			StTxtPara para2Curr = m_scrInMemoryCache.AddParaToMockedSectionContent(sectionCur.Hvo, ScrStyleNames.IntroParagraph);
			m_scrInMemoryCache.AddRunToMockedPara(para2Curr, "new stuff that doesn't match (K)", Cache.DefaultVernWs);
			int lenPara2Curr = para2Curr.Contents.Length;

			StTxtPara para3Curr = m_scrInMemoryCache.AddParaToMockedSectionContent(sectionCur.Hvo, ScrStyleNames.IntroParagraph);
			m_scrInMemoryCache.AddRunToMockedPara(para3Curr, "Finally, some conclusions about the letter C", Cache.DefaultVernWs);
