			VerifyFootnote(footnoteNew2, paraNew, 5);

			// Recheck that Current is now identical to Revision
			m_bookMerger.DetectDifferences_ReCheck();
			Assert.AreEqual(0, m_bookMerger.Differences.Count);
		}

