			IScrSection section2Curr = CreateSection(m_genesis, "Section Dos");
			int section2CurrHvo = section2Curr.Hvo;
			StTxtPara para2Curr = m_scrInMemoryCache.AddParaToMockedSectionContent(section2Curr.Hvo, ScrStyleNames.NormalParagraph);
			int para2CurrHvo = para2Curr.Hvo;
			m_scrInMemoryCache.AddRunToMockedPara(para2Curr, "12", ScrStyleNames.VerseNumber);
			m_scrInMemoryCache.AddRunToMockedPara(para2Curr, "doce ", Cache.DefaultVernWs);
