		/// ------------------------------------------------------------------------------------
		/// <summary>
		/// Test the <see cref="BookMerger.ReplaceCurrentWithRevision"/> method when a Scripture
		/// section in the Current is empty (containing a stanza break and two other empty paras)
		/// and the revision has content in multiple paras. Test for TE-8003
		/// </summary>
		/// ------------------------------------------------------------------------------------
		[Test]
		public void ReplaceCurWithRev_TE8003()
		{
			IScrSection sectionCur = m_scrInMemoryCache.AddSectionToMockedBook(m_genesis.Hvo);
			m_scrInMemoryCache.AddSectionHeadParaToSection(sectionCur.Hvo, "Section 1",
				ScrStyleNames.SectionHead);
			StTxtPara para1Cur = AddPara(sectionCur);
			AddVerse(para1Cur, 1, 0, string.Empty);
			StTxtPara para2Cur = AddEmptyPara(sectionCur, ScrStyleNames.StanzaBreak);
			StTxtPara para3Cur = AddEmptyPara(sectionCur, ScrStyleNames.Line1);
			sectionCur.AdjustReferences();

			IScrSection sectionRev = m_scrInMemoryCache.AddSectionToMockedBook(m_genesisRevision.Hvo);
			m_scrInMemoryCache.AddSectionHeadParaToSection(sectionRev.Hvo, "Section 1",
				ScrStyleNames.SectionHead);
			StTxtPara para1Rev = AddPara(sectionRev);
			AddVerse(para1Rev, 1, 0, "This is the first part");
			StTxtPara para2Rev = AddPara(sectionRev, ScrStyleNames.Line2);
			AddVerse(para2Rev, 0, 0, "of a two para verse.");
			StTxtPara para3Rev = AddEmptyPara(sectionRev, ScrStyleNames.StanzaBreak);
			StTxtPara para4Rev = AddPara(sectionRev, ScrStyleNames.Line1);
			AddVerse(para4Rev, 0, 0, "more text.");
			sectionRev.AdjustReferences();

			m_bookMerger.DetectDifferences(null);
			Assert.AreEqual(2, m_bookMerger.Differences.Count);
			Difference diff1 = m_bookMerger.Differences.MoveFirst();
			Assert.AreEqual(DifferenceType.ParagraphStructureChange, diff1.DiffType);
			Difference diff2 = m_bookMerger.Differences.MoveNext();
			Assert.AreEqual(DifferenceType.StanzaBreakAddedToCurrent, diff2.DiffType);

			// Revert the first difference -- content paragraphs restored to Current version
			Assert.AreEqual(3, sectionCur.ContentOA.ParagraphsOS.Count);
			m_bookMerger.ReplaceCurrentWithRevision(diff1);
			Assert.AreEqual(6, sectionCur.ContentOA.ParagraphsOS.Count);
			// Make sure that the paragraphs came in the expected order.
			Assert.AreEqual(ScrStyleNames.NormalParagraph,
				ScrStyleNames.GetStyleName(sectionCur.ContentOA.ParagraphsOS[0].Hvo, m_inMemoryCache.Cache));
			Assert.AreEqual(ScrStyleNames.Line2,
				ScrStyleNames.GetStyleName(sectionCur.ContentOA.ParagraphsOS[1].Hvo, m_inMemoryCache.Cache));
			Assert.AreEqual(ScrStyleNames.StanzaBreak,
				ScrStyleNames.GetStyleName(sectionCur.ContentOA.ParagraphsOS[2].Hvo, m_inMemoryCache.Cache));
			Assert.AreEqual(ScrStyleNames.Line1,
				ScrStyleNames.GetStyleName(sectionCur.ContentOA.ParagraphsOS[3].Hvo, m_inMemoryCache.Cache));
			Assert.AreEqual(ScrStyleNames.StanzaBreak,
				ScrStyleNames.GetStyleName(sectionCur.ContentOA.ParagraphsOS[4].Hvo, m_inMemoryCache.Cache));
			Assert.AreEqual(ScrStyleNames.Line1,
				ScrStyleNames.GetStyleName(sectionCur.ContentOA.ParagraphsOS[5].Hvo, m_inMemoryCache.Cache));

			// Revert the second difference -- remove added stanza break
			m_bookMerger.ReplaceCurrentWithRevision(diff2);
			Assert.AreEqual(5, sectionCur.ContentOA.ParagraphsOS.Count);
			// Confirm that we deleted the last stanza break
			Assert.AreEqual(ScrStyleNames.StanzaBreak,
				ScrStyleNames.GetStyleName(sectionCur.ContentOA.ParagraphsOS[2].Hvo, m_inMemoryCache.Cache));
			Assert.AreEqual(ScrStyleNames.Line1,
				ScrStyleNames.GetStyleName(sectionCur.ContentOA.ParagraphsOS[3].Hvo, m_inMemoryCache.Cache));
			// Since the ScrVerse iterator doesn't handle empty paragraphs, we have an empty paragraph at the
			// end.
			Assert.AreEqual(ScrStyleNames.Line1,
				ScrStyleNames.GetStyleName(sectionCur.ContentOA.ParagraphsOS[4].Hvo, m_inMemoryCache.Cache));
		}
