		/// ------------------------------------------------------------------------------------
		/// <summary>
		/// Helper method:
		/// Set up the test paragraph in the Revision, incl footnotes and back translations.
		/// </summary>
		/// ------------------------------------------------------------------------------------
		private StTxtPara SetUpParagraphInRevision()
		{
			// Build a paragraph in the revision book
			IScrSection sectionRev = CreateSection(m_genesisRevision, "My aching head!");
			StTxtPara para2Rev = m_scrInMemoryCache.AddParaToMockedSectionContent(sectionRev.Hvo, ScrStyleNames.NormalParagraph);
			m_scrInMemoryCache.AddRunToMockedPara(para2Rev, "2", ScrStyleNames.VerseNumber);
			m_scrInMemoryCache.AddRunToMockedPara(para2Rev, "verse two", Cache.DefaultVernWs);

			// and the second para includes a footnote
			StFootnote footnote2Rev = m_scrInMemoryCache.AddFootnote(m_genesisRevision, para2Rev, 1,
				"New footnote text");

			// Add back translation to the paragraph, and its status
			int btWs = Cache.DefaultAnalWs;
			ICmTranslation paraTrans = m_inMemoryCache.AddBtToMockedParagraph(para2Rev, btWs);
			m_inMemoryCache.AddRunToMockedTrans(paraTrans, btWs, "BT of verse two", null);
			paraTrans.Status.SetAlternative(BackTranslationStatus.Checked.ToString(), btWs);

			// Add back translation of footnote, and its status
			ICmTranslation footnoteTrans = m_scrInMemoryCache.AddFootnoteORCtoTrans(paraTrans, 2, btWs,
				footnote2Rev, "BT of footnote");
			footnoteTrans.Status.SetAlternative(BackTranslationStatus.Finished.ToString(), btWs);

			return para2Rev;
		}

		/// ------------------------------------------------------------------------------------
		/// <summary>
		/// Helper method:
		/// Verify the given copied paragraph, including footnotes and back translation, for
		/// any problems that the ChangeWatcher might cause.
		/// </summary>
		/// ------------------------------------------------------------------------------------
		private void VerifyCopiedPara(StTxtPara newPara)
		{
			int btWs = Cache.DefaultAnalWs;

			// Check the paragraph BT
			// para must have only only 1 translation, the BT
			Assert.AreEqual(1, newPara.TranslationsOC.Count);
			ICmTranslation paraTrans = newPara.GetBT();
