		/// ------------------------------------------------------------------------------------
		/// <summary>
		/// Test RecalculateDifferences when a missing para has been reviewed, and it originally
		/// referred to a Current paragraph that is later deleted before the Recalc.
		/// Verify that the reviewed "missing para" does not reappear on the recalculated diff list.
		///
		/// revision   current
		///               1
		///     2
		///     3         3
		///
		/// we will review 2, remove 1, then recalculate differences.
		/// </summary>
		/// ------------------------------------------------------------------------------------
		[Test]
		public void RecalculateDifferences_Paragraphs_DeleteProblem()
		{
			CheckDisposed();

			// Create a section for both the current version of Genesis and the stored revision.
			IScrSection sectionCur = m_scrInMemoryCache.AddSectionToMockedBook(m_genesis.Hvo);
			m_scrInMemoryCache.AddSectionHeadParaToSection(sectionCur.Hvo, "My aching head!", ScrStyleNames.SectionHead);

			IScrSection sectionRev = m_scrInMemoryCache.AddSectionToMockedBook(m_genesisRevision.Hvo);
			m_scrInMemoryCache.AddSectionHeadParaToSection(sectionRev.Hvo, "My aching head!", ScrStyleNames.SectionHead);

			// build Current section
			StTxtPara para1Curr = m_scrInMemoryCache.AddParaToMockedSectionContent(sectionCur.Hvo, ScrStyleNames.NormalParagraph);
			m_scrInMemoryCache.AddRunToMockedPara(para1Curr, "1", ScrStyleNames.VerseNumber);
			m_scrInMemoryCache.AddRunToMockedPara(para1Curr, "verse one", Cache.DefaultVernWs);

			StTxtPara para2Curr = m_scrInMemoryCache.AddParaToMockedSectionContent(sectionCur.Hvo, ScrStyleNames.NormalParagraph);
			m_scrInMemoryCache.AddRunToMockedPara(para2Curr, "3", ScrStyleNames.VerseNumber);
			m_scrInMemoryCache.AddRunToMockedPara(para2Curr, "verse three", Cache.DefaultVernWs);
			sectionCur.AdjustReferences();

			// build Revision section
			StTxtPara para1Rev = m_scrInMemoryCache.AddParaToMockedSectionContent(sectionRev.Hvo, ScrStyleNames.NormalParagraph);
			m_scrInMemoryCache.AddRunToMockedPara(para1Rev, "2", ScrStyleNames.VerseNumber);
			m_scrInMemoryCache.AddRunToMockedPara(para1Rev, "verse two", Cache.DefaultVernWs);

			StTxtPara para2Rev = m_scrInMemoryCache.AddParaToMockedSectionContent(sectionRev.Hvo, ScrStyleNames.NormalParagraph);
			m_scrInMemoryCache.AddRunToMockedPara(para2Rev, "3", ScrStyleNames.VerseNumber);
			m_scrInMemoryCache.AddRunToMockedPara(para2Rev, "verse three", Cache.DefaultVernWs);
			sectionRev.AdjustReferences();

			// Detect differences
			m_bookMerger.DetectDifferences(null);
			Assert.AreEqual(2, m_bookMerger.Differences.Count);

			// verify the differences
			// verse 1 para is added to current
			Difference diff1 = m_bookMerger.Differences.MoveFirst();
			Assert.AreEqual(DifferenceType.ParagraphAddedToCurrent, diff1.DiffType);
			Assert.AreEqual(01001001, diff1.RefStart);

			// verse 2 para is missing in current
			Difference diff2 = m_bookMerger.Differences.MoveNext();
			Assert.AreEqual(DifferenceType.ParagraphMissingInCurrent, diff2.DiffType);
			Assert.AreEqual(01001002, diff2.RefStart);
			// verify that a potential problem exists:
			//  diff2 points at Current para 1, the way DetectDiffs works
			Assert.AreEqual(para1Curr.Hvo, diff2.HvoCurr);

			// review difference 2
			m_bookMerger.MarkDifferenceAsReviewed(diff2);

			// revert diff 1 which will delete para 1 from the current.
			m_bookMerger.ReplaceCurrentWithRevision(diff1);
			Assert.AreEqual(1, sectionCur.ContentOA.ParagraphsOS.Count);
			//Assert.AreEqual(para2Curr.Hvo, diff2.HvoCurr); //diff2 should be fixed to point at Current para 2
			// at this point, there should be no more diffs to be processed
			Assert.AreEqual(0, m_bookMerger.Differences.Count);

			// Recalculate diffs and make sure there are no differences detected
			m_bookMerger.RecalculateDifferences();

			// The RecalculateDifferences() should have removed from the diff list all items
			// that had already been reviewed, such as verse 2 para "missing in current".
			// However, we are concerned because verse 1 para is now gone, and so the new diff
			// object for verse 2 para "missing in current" will now refer to a different curr para.
			// Verify that the reviewed "missing para" does not reappear on the recalculated diff list.
			Assert.AreEqual(0, m_bookMerger.Differences.Count);
		}
